import React,{useEffect,useState} from 'react';
import Containeritems from './Containeritems';
import Carouselitems from './Carouselitems';
import PropTypes from 'prop-types';
function Container(props) {

  const [articles,setArticles] = useState([]);
  const [limit,setLimit] = useState(6);
  const [no,setNo] = useState(0);
  const [image,setImage] = useState("https://fakestoreapi.com/img/81fPKd-2AYL._AC_SL1500_.jpg")

  const updateData = async ()=>{
    const url  = `https://fakestoreapi.com/products/${props.category?"category/"+props.category:""}?limit=${limit}`;
    let data = await fetch(url);
    let parsedData = await data.json();
    setArticles(parsedData);
  }
  useEffect(() => {
    updateData();
    }, [])

const handleNextClick = ()=>{

setLimit(limit+3);
if(limit==21){
setLimit(limit-3);
}else{
  updateData();
}
}

const Cprev = () =>{
  if(no==0){
  }else{
    setNo(no-1);
    setImage(articles[no].image);
    updateData();
  }
}

const Cnext = () => {
  setNo(no+1);
  setImage(articles[no].image);
  updateData()
}
const handlePrevClick = ()=>{

setLimit(limit-3);
if(limit==3){
setLimit(limit+3);
}else{
  updateData();
}
}

  return (
    <>
    <div>
<Carouselitems
image1={image}
></Carouselitems>
    </div>
    <div className=" my-4">
      <div className="container">
      <div className="row">
        {articles.map((element) => {
          return (
            <div className="col-md-4" key={element.id}>
<Containeritems
title = {element.title?element.title.slice(0,44) + "...":""}
imageUrl = {element.image}
description = {element.description?element.description.slice(0,88) + "...":""}
price = {element.price?element.price+"$":"$"}
rating ={element.rating.rate?element.rating.rate:"**"}
reviews = {element.rating.count?element.rating.count:"**"}
></Containeritems>
            </div>
          )
        })}
      </div>
    </div>
    </div>
    <div className="container d-flex justify-content-between mb-5">
<button  type="button" className="btn btn-dark" onClick={handlePrevClick}>
&larr; Previous{" "}
</button>
<button id="nxt" type="button" className="btn btn-dark" onClick={handleNextClick}>Next &rarr;</button>
</div> 
<br />

<button onClick={href} className='back'>
  <a style={{color:"white"}} href='#'>Back to Top</a>
</button> </>
    )
}

export default Container
