import React, { useEffect, useState } from "react";
import Containeritems from "./Containeritems";
import Carouselitems from "./Car";
import PropTypes from "prop-types";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Footer from "./Footer";
import "./Container.css";

function Container(props) {
  const [articles, setArticles] = useState([]);
  const [limit, setLimit] = useState(3);
  const [articles1, setArticles1] = useState([]);

  const updateData = async () => {
    const url = `https://fakestoreapi.com/products/${
      props.category ? "category/" + props.category : ""
    }?limit=${limit}`;
    let data = await fetch(url);
    let parsedData = await data.json();
    setArticles(parsedData);
    const url1 = "https://fakestoreapi.com/products";
    let data1 = await fetch(url1);
    let parsedData1 = await data1.json();
    setArticles1(parsedData1);
  };

  useEffect(() => {
    updateData();
  }, []);

  const handleNextClick = () => {
    setLimit(limit + 3);
    if (limit === 21) {
      setLimit(limit - 3);
    } else {
      updateData();
    }
  };

  const handlePrevClick = () => {
    setLimit(limit - 3);
    if (limit === 3) {
      document.getElementById("prev").disabled = true;
      setLimit(limit + 3);
    } else {
      updateData();
    }
  };

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
        }
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        }
      }
    ]
  };

  return (
    <div className="container-wrapper">
      <div className="category-title">
        <h1>{props.category ? props.category : "All Products"}</h1>
      </div>
      <div className="products-grid">
        {articles.map((element) => (
          <div className="product-item" key={element.id}>
            <Containeritems
              title={element.title ? element.title.slice(0, 24) + "..." : ""}
              imageUrl={element.image}
              description={element.description ? element.description.slice(0, 60) + "..." : ""}
              price={element.price ? element.price + "$" : "$"}
              rating={element.rating.rate ? element.rating.rate : "**"}
              reviews={element.rating.count ? element.rating.count : "**"}
            />
          </div>
        ))}
      </div>
      <div className="pagination-buttons">
        <button
          id="prev"
          className="btn btn-primary custom-btn"
          onClick={handlePrevClick}
        >
          &larr; Previous
        </button>
        <button
          id="nxt"
          className="btn btn-primary custom-btn"
          onClick={handleNextClick}
        >
          Next &rarr;
        </button>
      </div>
      <div className="featured-products">
        <h2>Featured Products</h2>
        <Slider {...settings}>
          {articles1.map((e) => (
            <div key={e.id}>
              <Carouselitems
                title={e.title ? e.title.slice(0, 24) + "..." : ""}
                description={e.description ? e.description.slice(0, 60) + "..." : ""}
                image={e.image}
              />
            </div>
          ))}
        </Slider>
      </div>
    </div>
  );
}

Container.propTypes = {
  category: PropTypes.string
};

export default Container;
