import React,{useState,useEffect} from 'react'

function Carousel(props) {
  const [articles,setArticles] = useState([]);
  const [no,setNo] = useState(1);
  const [image,setImage] = useState("https://fakestoreapi.com/img/71li-ujtlUL._AC_UX679_.jpg");
  const [title,setTitle] = useState("Solid Gold Petite Micropave ");

  const updateData = async ()=>{
    const url  = `https://fakestoreapi.com/products/${props.category?"category/"+props.category:""}`;
    let data = await fetch(url);
    let parsedData = await data.json();
    setArticles(parsedData);
    setImage(articles[no].image);
    setTitle(articles[no].title);
  }

const Cprev = () =>{

  if(no==0){
  }else{
setNo(no-1);
updateData();
  }
}

const Cnext = () => {
  setNo(no+1);
updateData();
}
let {image1} = props;
  return (
<>
<div id="carouselExampleFade" class="carousel slide carousel-fade">
  <div class="carousel-inner">
    <div class="carousel-item active">
<div className='container d-flex justify-content-center align-items-center flex-column'>
        <img src={image} class="d-block w-100 siz" alt="..."/>
        <h3 className='colo'>{title}</h3>
</div>
    </div>
    <div class="carousel-item ">
    <div className='container d-flex justify-content-center align-items-center flex-column'>
      <img src={image} class="d-block w-100 siz" alt="..."/>
      <h3 className='colo'>{title}</h3>
      </div>
    </div>
    <div class="carousel-item">
    <div className='container d-flex justify-content-center align-items-center flex-column'>
      <img src={image} class="d-block w-100 siz" alt="..."/>
      <h3 className='colo'>{title}</h3>
      </div>
    </div>
  </div>
  <button onClick={Cprev} class="carousel-control-prev bl" type="button" data-bs-target="#carouselExampleFade" data-bs-slide="prev">
    <span class="carousel-control-prev-icon" aria-hidden="true"></span>
    <span class="visually-hidden">Previous</span>
  </button>
  <button onClick={Cnext} class="carousel-control-next bl" type="button" data-bs-target="#carouselExampleFade" data-bs-slide="next">
    <span class="carousel-control-next-icon" aria-hidden="true"></span>
    <span class="visually-hidden">Next</span>
  </button>
</div>
</>
  )
}

export default Carousel
