import React,{} from 'react'
import { Link } from 'react-router-dom';
import propTypes from 'prop-types';
function Signup() {
    const [form,setForm] = useState({})
    const [users,setUsers] = useState([]);
  
  
  
      const showP = () => {
          if(document.getElementById("cb").checked){
              document.getElementById("password").type="text";
              console.log("checked");
          }else{
              document.getElementById("password").type="password";
              console.log("not checked");
          }
      }
  
  const handleForm = (e) => {
  setForm({ 
    ...form,
     [e.target.name] : e.target.value,
    })
  }
  
  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch("http://localhost:3080/",{
      method : "POST",
      body : JSON.stringify(form),
      headers:{
        "Content-Type": "application/json"
      }
    });
    const data = await response.json();
    setUsers(data);
    alert("logged in successfully");
  }
  
  const getUsers = async (req, res) => {
    const response = await fetch("http://localhost:3080/",{
      method : "GET",
    });
    const data = await response.json();
    console.log(data);
  }
  
  useEffect(()=>{
    getUsers();
  },[])
  
  return (
<div className='df justify-content-center'>
    <form onSubmit={handleSubmit} className='log'>
    <div id="container">
  <h1 id='h'>Sign up</h1>
  <input id="mail" onChange={handleForm} type="email" name='mail' placeholder="Email Address" />
  <input id="password" onChange={handleForm} name='password' type="password" placeholder="Password" />
  <div id="checkbox">
    <input onClick={showP} id="cb" type="checkbox"/>
    show password
  </div>
  <button onClick={(e)=>{
    e.preventDefault();
  }} id="bn">Sign up</button>
  <a className='fp' href="#">Forgot password?</a>
  <p className='Ac' style={{color:"white"}}>Already have an account</p>
    <Link  className='fp rot' to="/login" >Log in</Link>
</div>
    </form>
</div>
  )

}

export default Signup
