import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
function Login(props) {
  let { prevent } = props;

  const [form, setForm] = useState({});
  const [users, setUsers] = useState([]);

  const showP = () => {
    if (document.getElementById("cb").checked) {
      document.getElementById("password").type = "text";
      console.log("checked");
    } else {
      document.getElementById("password").type = "password";
      console.log("not checked");
    }
  };

  const handleForm = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch("http://localhost:3080/", {
      method: "POST",
      body: JSON.stringify(form),
      headers: {
        "Content-Type": "application/json",
      },
    });
    const data = await response.json();
    setUsers(data);
    alert("logged in successfully");
  };

  const getUsers = async (req, res) => {
    const response = await fetch("http://localhost:3080/", {
      method: "GET",
    });
    const data = await response.json();
    console.log(data);
  };

  useEffect(() => {
    getUsers();
  }, []);

  return (
    <>
      <div className="df container justify-content-center">
        <form onSubmit={handleSubmit} className="log">
          <div id="container">
            <h1 id="h">Log In</h1>
            <input
              id="mail"
              onChange={handleForm}
              name="mail"
              type="email"
              placeholder="Email Address"
            />
            <input
              id="password"
              onChange={handleForm}
              name="password"
              type="password"
              placeholder="Password"
            />
            <div id="checkbox">
              <input onClick={showP} id="cb" type="checkbox" />
              show password
            </div>
            <button id="bn">Log in</button>
            <a className="fp" href="#">
              Forgot password?
            </a>
            <div>
              <p className="Ac" style={{ color: "white" }}>
                Don't have an account
              </p>
            </div>
            <Link className="fp " to="/signup">
              Sign up
            </Link>
          </div>
        </form>
      </div>
      {/* <div>
  <ul>
    {users.map((e)=>{
      <li>{e.username},{e.password}</li>
    })}
  </ul>
</div> */}
    </>
  );
}

export default Login;
